import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import welcomeStyle from './Welcome.module.css';
import { Worker, Viewer } from '@react-pdf-viewer/core';
import '@react-pdf-viewer/core/lib/styles/index.css';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';

const Welcome = ({ user, setUserState }) => {
    const [books, setBooks] = useState([]);
    const [search, setSearch] = useState('');
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [file, setFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);
    const [showAddBook, setShowAddBook] = useState(false);
    const [selectedBookUrl, setSelectedBookUrl] = useState(null);
    const defaultLayoutPluginInstance = defaultLayoutPlugin();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/books', {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        }).then(res => {
            setBooks(res.data);
        }).catch(err => {
            console.error(err);
        });
    }, []);

    const searchHandler = (e) => {
        e.preventDefault();
        axios.get(`http://127.0.0.1:5000/books/search?q=${search}`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        }).then(res => {
            setBooks(res.data);
        }).catch(err => {
            console.error(err);
        });
    };

    const addBookHandler = (e) => {
        e.preventDefault();
        setIsUploading(true);
        const formData = new FormData();
        formData.append('title', title);
        formData.append('author', author);
        formData.append('file', file);

        axios.post('http://127.0.0.1:5000/add-book', formData, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`,
                'Content-Type': 'multipart/form-data'
            }
        }).then(res => {
            console.log(res.data);
            alert('Book added successfully');
            setIsUploading(false);
            setTitle('');
            setAuthor('');
            setFile(null);
            setShowAddBook(false);
        }).catch(err => {
            console.error(err);
            alert('Failed to add book');
            setIsUploading(false);
        });
    };

    const logout = () => {
        setUserState({});
        localStorage.removeItem('token');
        navigate('/login', { replace: true });
    };

    return (
        <div className={welcomeStyle.welcome}>
            <div className={welcomeStyle.sidebar}>
                <button onClick={() => setShowAddBook(false)}>Books</button>
                {user.is_admin && (
                    <button onClick={() => setShowAddBook(true)}>Add Book</button>
                )}
                <button onClick={logout}>Logout</button>
            </div>
            <div className={welcomeStyle.content}>
                {!showAddBook ? (
                    <div className={welcomeStyle.searchSection}>
                        <h2>Search Books</h2>
                        <form onSubmit={searchHandler}>
                            <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} />
                            <button type="submit">Search</button>
                        </form>
                        <div className={welcomeStyle.booksList}>
                            {books.map(book => (
                                <div key={book.id} className={welcomeStyle.bookItem}>
                                    <h3>{book.title}</h3>
                                    <p>{book.author}</p>
                                    <button onClick={() => setSelectedBookUrl(book.file_url)}>View</button>
                                    <a href={book.file_url} download>Download</a>
                                </div>
                            ))}
                        </div>
                        {selectedBookUrl && (
                            <div className={welcomeStyle.pdfViewer}>
                                <Worker workerUrl={`https://unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`}>
                                    <Viewer fileUrl={selectedBookUrl} plugins={[defaultLayoutPluginInstance]} />
                                </Worker>
                            </div>
                        )}
                    </div>
                ) : (
                    <div className={welcomeStyle.adminSection}>
                        <h2>Add Book</h2>
                        <form onSubmit={addBookHandler}>
                            <div>
                                <label>Title</label>
                                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
                            </div>
                            <div>
                                <label>Author</label>
                                <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
                            </div>
                            <div>
                                <label>File</label>
                                <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
                            </div>
                            <button type="submit" disabled={isUploading}>Add Book</button>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Welcome;
