import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import welcomestyle from "./Welcome.module.css";
import axios from 'axios';

const Welcome = ({ user, setUserState }) => {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [message, setMessage] = useState('');
  const [activeSection, setActiveSection] = useState('search'); // Add state to track active section

  useEffect(() => {
    console.log("User data in Welcome component:", user);
  }, [user]);

  const logout = () => {
    setUserState(null);
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  useEffect(() => {
    if (user) {
      axios.get("http://ec2-3-107-93-162.ap-southeast-2.compute.amazonaws.com:5000/books", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      }).then(res => {
        setBooks(res.data);
      }).catch(err => {
        console.error(err);
      });
    }
  }, [user]);

  const searchHandler = (e) => {
    e.preventDefault();
    axios.get('127.0.0.1:5000/books/search?q=${search}', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleAddBook = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('author', author);

    axios.post("127.0.0.1:5000/books", formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then((res) => {
      setMessage(res.data.message);
      setBooks([...books, res.data.book]);
    }).catch((err) => {
      setMessage(err.response.data.message);
    });
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className={welcomestyle.welcome}>
      <h1>Welcome, {user.fname} {user.lname}</h1>
      <button onClick={logout}>Logout</button>

      {user.is_admin && (
        <div>
          <button onClick={() => setActiveSection('add')}>Add Book</button>
          <button onClick={() => setActiveSection('search')}>Search Books</button>
        </div>
      )}

      {activeSection === 'add' && user.is_admin && (
        <div>
          <h2>Add Book</h2>
          <form onSubmit={handleAddBook}>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              type="text"
              placeholder="Author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
            <input
              type="file"
              onChange={handleFileChange}
            />
            <button type="submit">Add Book</button>
          </form>
          {message && <p>{message}</p>}
        </div>
      )}

      {activeSection === 'search' && (
        <div>
          <h2>Books</h2>
          <form onSubmit={searchHandler}>
            <input
              type="text"
              placeholder="Search books"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
          <ul>
            {books.map(book => (
              <li key={book.id}>
                {book.title} by {book.author} - <a href={book.file_url} download>Download</a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Welcome;
