import React, { useState, useEffect } from "react";
import basestyle from "../../css/Base.module.css";
import registerstyle from "./Register.module.css";
import axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";

const Register = () => {
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [isCodeSent, setIsCodeSent] = useState(false);
  const [verificationCode, setVerificationCode] = useState("");
  const [isEmailVerified, setIsEmailVerified] = useState(false);
  const [verificationMessage, setVerificationMessage] = useState("");
  const [user, setUserDetails] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    cpassword: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s+@]+\@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.fname) {
      error.fname = "First Name is required";
    }
    if (!values.lname) {
      error.lname = "Last Name is required";
    }
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "This is not a valid email format!";
    }
    if (!values.password) {
      error.password = "Password is required";
    } else if (values.password.length < 4) {
      error.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      error.password = "Password cannot exceed more than 10 characters";
    }
    if (!values.cpassword) {
      error.cpassword = "Confirm Password is required";
    } else if (values.cpassword !== values.password) {
      error.cpassword = "Confirm password and password should be same";
    }
    return error;
  };

  const signupHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));
    setIsSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      if (!isEmailVerified) {
        alert("Please verify your email first.");
      } else {
        axios.post("http://127.0.0.1:5000/signup", user).then((res) => {
          alert(res.data.message);
          navigate("/login", { replace: true });
        }).catch((error) => {
          alert("Error: " + error.message);
        });
      }
    }
  }, [formErrors, isSubmit, isEmailVerified, navigate, user]);

  const sendVerificationCode = () => {
    axios.post("http://127.0.0.1:5000/send-verification-code", { email: user.email }).then((res) => {
      alert(res.data.message);
      setIsCodeSent(true);
    }).catch((error) => {
      alert("Error: " + error.message);
    });
  };

  const verifyCodeHandler = () => {
    axios.post("http://127.0.0.1:5000/verify-code", { email: user.email, code: verificationCode }).then((res) => {
      if (res.data.success) {
        setIsEmailVerified(true);
        setVerificationMessage("Email verified successfully. You can now register.");
      } else {
        setVerificationMessage("Invalid verification code. Please try again.");
      }
    }).catch((error) => {
      setVerificationMessage("Error: " + error.message);
    });
  };

  return (
    <div className={registerstyle.register}>
      <form>
        <h1>Create your account</h1>
        <div className={registerstyle.registerInputContainer}>
          <label className={registerstyle.registerLabel}>First Name</label>
          <input
            type="text"
            name="fname"
            placeholder="First Name"
            onChange={changeHandler}
            value={user.fname}
            className={registerstyle.registerInputField}
          />
          <p className={basestyle.error}>{formErrors.fname}</p>
        </div>
        <div className={registerstyle.registerInputContainer}>
          <label className={registerstyle.registerLabel}>Last Name</label>
          <input
            type="text"
            name="lname"
            placeholder="Last Name"
            onChange={changeHandler}
            value={user.lname}
            className={registerstyle.registerInputField}
          />
          <p className={basestyle.error}>{formErrors.lname}</p>
        </div>
        <div className={registerstyle.registerInputContainer}>
          <label className={registerstyle.registerLabel}>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={changeHandler}
            value={user.email}
            className={registerstyle.registerInputField}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
          <p className={registerstyle.verifyText} onClick={sendVerificationCode}>
            Verify Email
          </p>
        </div>
        {isCodeSent && (
          <div className={registerstyle.registerInputContainer}>
            <label className={registerstyle.registerLabel}>Enter Verification Code</label>
            <input
              type="text"
              name="verificationCode"
              placeholder="Verification Code"
              onChange={(e) => setVerificationCode(e.target.value)}
              value={verificationCode}
              className={registerstyle.registerInputField}
            />
            <p className={registerstyle.verifyCodeText} onClick={verifyCodeHandler}>
              Verify Code
            </p>
            <p className={registerstyle.verificationMessage}>{verificationMessage}</p>
          </div>
        )}
        <div className={registerstyle.registerInputContainer}>
          <label className={registerstyle.registerLabel}>Password</label>
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={changeHandler}
            value={user.password}
            className={registerstyle.registerInputField}
          />
          <p className={basestyle.error}>{formErrors.password}</p>
        </div>
        <div className={registerstyle.registerInputContainer}>
          <label className={registerstyle.registerLabel}>Confirm Password</label>
          <input
            type="password"
            name="cpassword"
            placeholder="Confirm Password"
            onChange={changeHandler}
            value={user.cpassword}
            className={registerstyle.registerInputField}
          />
          <p className={basestyle.error}>{formErrors.cpassword}</p>
        </div>
        <button
          className={`${basestyle.button_common} ${registerstyle.registerButton}`}
          onClick={signupHandler}
          disabled={!isEmailVerified}  // Disable the button until email is verified
        >
          Register
        </button>
        <div className={registerstyle.newUser}>
          <span>Already registered?</span>
          <NavLink to="/login" className={registerstyle.signupButton}>
            Login Now
          </NavLink>
        </div>
      </form>
    </div>
  );
};

export default Register;
