import React, { useState } from "react";
import basestyle from "../../css/Base.module.css";
import verificationstyle from "./EmailVerification.module.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EmailVerification = () => {
  const navigate = useNavigate();
  const [code, setCode] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setCode(value);
  };

  const validateForm = (value) => {
    const error = {};
    if (!value) {
      error.code = "Code is required";
    }
    return error;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(code));
    setIsSubmit(true);
  };

  const handleCodeVerification = () => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://127.0.0.1:5000/verify-code", { code }).then((res) => {
        alert(res.data.message);
        navigate("/reset-password", { replace: true });
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  };

  return (
    <div className={verificationstyle.verification}>
      <form onSubmit={submitHandler}>
        <h1>Email Verification</h1>
        <div className={verificationstyle.inputContainer}>
          <label>Enter Code</label>
          <input
            type="text"
            name="code"
            placeholder="Enter verification code"
            onChange={changeHandler}
            value={code}
            className={verificationstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.code}</p>
        </div>
        <button className={`${basestyle.button_common} ${verificationstyle.submitButton}`} type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default EmailVerification;
