import React, { useState, useEffect } from "react";
import basestyle from "../../css/Base.module.css";
import loginstyle from "./Login.module.css";
import background from '../../assets/background.jpg';  // Import the image
import axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';

const Login = ({ setUserState }) => {
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [user, setUserDetails] = useState({
    email: "",
    password: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s+@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "Please enter a valid email address";
    }
    if (!values.password) {
      error.password = "Password is required";
    }
    return error;
  };

  const loginHandler = (e) => {
    e.preventDefault(); // Prevent default form submission
    setFormErrors(validateForm(user));
    setIsSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://3.27.222.116/login", user).then((res) => {
        if (res.data.success) {
          setUserState(res.data.user);
          navigate("/", { replace: true });
        } else {
          alert(res.data.message || "Login failed");
        }
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  }, [formErrors]);

  const handleGoogleLoginSuccess = (credentialResponse) => {
    // Handle Google login success, send the credential to the backend
    axios.post("http://3.27.222.116/google-login", { credential: credentialResponse.credential }).then((res) => {
      if (res.data.success) {
        setUserState(res.data.user);
        navigate("/", { replace: true });
      } else {
        alert(res.data.message || "Google Login failed");
      }
    }).catch((error) => {
      alert("Error: " + error.message);
    });
  };

  const handleGoogleLoginFailure = () => {
    alert("Google Login failed");
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <div className={loginstyle.login} style={{ backgroundImage: `url(${background})` }}>
        <form onSubmit={loginHandler}>
          <h1>Login</h1>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={changeHandler}
            value={user.email}
            className={loginstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={changeHandler}
            value={user.password}
            className={loginstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.password}</p>
          <button type="submit" className={basestyle.button_common}>
            Login
          </button>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={handleGoogleLoginFailure}
            className={loginstyle.googleLoginButton}
          />
          <NavLink to="/signup" className={loginstyle.registerLink}>Not yet registered? Register Now</NavLink>
        </form>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
