import React, { useState, useEffect } from 'react';
import axios from 'axios';
import welcomeStyle from './Welcome.module.css';
import { useNavigate } from 'react-router-dom';

const Welcome = ({ user, setUserState }) => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [file, setFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/books', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  }, []);

  const searchHandler = (e) => {
    e.preventDefault();
    axios.get(`http://127.0.0.1:5000/books/search?q=${search}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  };

  const addBookHandler = (e) => {
    e.preventDefault();
    setIsUploading(true);

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('file', file);

    axios.post('http://127.0.0.1:5000/add-book', formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
        'Content-Type': 'multipart/form-data'
      }
    }).then(res => {
      console.log(res.data);
      alert('Book added successfully');
      setIsUploading(false);
      setTitle('');
      setAuthor('');
      setFile(null);
      setBooks([...books, res.data]);
    }).catch(err => {
      console.error(err);
      alert('Failed to add book');
      setIsUploading(false);
    });
  };

  const logout = () => {
    setUserState({});
    localStorage.removeItem('token');
    navigate('/login', { replace: true });
  };

  return (
    <div className={welcomeStyle.container}>
      <aside className={welcomeStyle.sidebar}>
        <button className={welcomeStyle.sidebarButton} onClick={() => navigate('/')}>BOOKS</button>
        <button className={welcomeStyle.sidebarButton} onClick={() => navigate('/categories')}>Category</button>
        <button className={welcomeStyle.sidebarButton} onClick={() => navigate('/downloads')}>Downloads</button>
      </aside>
      <main className={welcomeStyle.mainContent}>
        <div className={welcomeStyle.header}>
          <h1>Welcome, {user.fname} {user.lname}</h1>
          <button onClick={logout}>Logout</button>
        </div>

        {user.is_admin && (
          <div className={welcomeStyle.adminSection}>
            <h2>Add Book</h2>
            <form onSubmit={addBookHandler}>
              <div>
                <label>Title</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
              </div>
              <div>
                <label>Author</label>
                <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
              </div>
              <div>
                <label>File</label>
                <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
              </div>
              <button type="submit" disabled={isUploading}>{isUploading ? 'Uploading...' : 'Add Book'}</button>
            </form>
          </div>
        )}

        <div className={welcomeStyle.searchSection}>
          <h2>Search Books</h2>
          <form onSubmit={searchHandler}>
            <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} />
            <button type="submit">Search</button>
          </form>
          <div className={welcomeStyle.booksList}>
            {books.map(book => (
              <div key={book.id} className={welcomeStyle.bookItem}>
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <a href={book.file_url} download className={welcomeStyle.downloadButton}>Download</a>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
