import React, { useState } from "react";
import basestyle from "../../css/Base.module.css";
import resetstyle from "./ResetPassword.module.css";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";

const ResetPassword = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [formErrors, setFormErrors] = useState({});
  const [message, setMessage] = useState("");
  const [user, setUserDetails] = useState({
    password: "",
    cpassword: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    if (!values.password) {
      error.password = "Password is required";
    } else if (values.password.length < 4) {
      error.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      error.password = "Password cannot exceed more than 10 characters";
    }
    if (!values.cpassword) {
      error.cpassword = "Confirm Password is required";
    } else if (values.cpassword !== values.password) {
      error.cpassword = "Confirm password and password should be the same";
    }
    return error;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));

    if (Object.keys(formErrors).length === 0) {
      const token = new URLSearchParams(location.search).get("token");
      axios.post("http://127.0.0.1:5000/reset-password", { ...user, token })
        .then((res) => {
          setMessage("Password has been reset successfully. Redirecting to login...");
          setTimeout(() => {
            navigate("/login", { replace: true });
          }, 3000);
        })
        .catch((error) => {
          setMessage("Error resetting password. Please try again.");
        });
    }
  };

  return (
    <div className={resetstyle.resetPassword}>
      <form onSubmit={submitHandler}>
        <h1>Reset Password</h1>
        <div className={resetstyle.inputContainer}>
          <label>New Password</label>
          <input
            type="password"
            name="password"
            placeholder="Enter new password"
            onChange={changeHandler}
            value={user.password}
            className={resetstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.password}</p>
        </div>
        <div className={resetstyle.inputContainer}>
          <label>Confirm Password</label>
          <input
            type="password"
            name="cpassword"
            placeholder="Confirm new password"
            onChange={changeHandler}
            value={user.cpassword}
            className={resetstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.cpassword}</p>
        </div>
        <button className={`${basestyle.button_common} ${resetstyle.submitButton}`} type="submit">
          Reset Password
        </button>
        <p className={resetstyle.message}>{message}</p>
      </form>
    </div>
  );
};

export default ResetPassword;
