import React, { useState } from 'react';
import axios from 'axios';
import forgotPasswordStyle from './ForgotPassword.module.css';
import { useNavigate } from 'react-router-dom';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const [step, setStep] = useState(1); // 1: Request Code, 2: Verify Code and Reset Password
  const navigate = useNavigate();

  const handleForgotPassword = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/forgot-password', { email })
      .then((response) => {
        setMessage(response.data.message);
        setStep(2); // Proceed to the next step
      })
      .catch((error) => {
        setMessage(error.response.data.message);
      });
  };

  const handleVerifyCode = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/verify-reset-code', { email, code: verificationCode })
      .then((response) => {
        setMessage(response.data.message);
        if (response.data.message === 'Code verified successfully.') {
          handleResetPassword();
        }
      })
      .catch((error) => {
        setMessage(error.response.data.message);
      });
  };

  const handleResetPassword = () => {
    axios.post('http://127.0.0.1:5000/reset-password', { email, code: verificationCode, new_password: newPassword })
      .then((response) => {
        setMessage(response.data.message);
        if (response.data.message === 'Password reset successfully.') {
          navigate('/login');
        }
      })
      .catch((error) => {
        setMessage(error.response.data.message);
      });
  };

  return (
    <div className={forgotPasswordStyle.forgotPassword}>
      {step === 1 && (
        <form onSubmit={handleForgotPassword}>
          <h1>Forgot Password</h1>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
          {message && <p>{message}</p>}
        </form>
      )}
      {step === 2 && (
        <form onSubmit={handleVerifyCode}>
          <h1>Verify Code and Reset Password</h1>
          <input
            type="text"
            placeholder="Enter verification code"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
          <button type="submit">Verify and Reset Password</button>
          {message && <p>{message}</p>}
        </form>
      )}
    </div>
  );
};

export default ForgotPassword;
