import React, { useState } from "react";
import basestyle from "../../css/Base.module.css";
import forgotstyle from "./ForgotPassword.module.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ForgotPassword = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setEmail(value);
  };

  const validateForm = (value) => {
    const error = {};
    const regex = /^[^\s+@]+\@[^\s@]+\.[^\s@]{2,}$/i;
    if (!value) {
      error.email = "Email is required";
    } else if (!regex.test(value)) {
      error.email = "Please enter a valid email address";
    }
    return error;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(email));
    setIsSubmit(true);
  };

  const handleEmailVerification = () => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://127.0.0.1:5000/forgot-password", { email }).then((res) => {
        alert(res.data.message);
        navigate("/email-verification", { replace: true });
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  };

  return (
    <div className={forgotstyle.forgot}>
      <form onSubmit={submitHandler}>
        <h1>Forgot Password</h1>
        <div className={forgotstyle.inputContainer}>
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            onChange={changeHandler}
            value={email}
            className={forgotstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
        </div>
        <button className={`${basestyle.button_common} ${forgotstyle.submitButton}`} type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default ForgotPassword;
