import React, { useState, useEffect } from "react";
import basestyle from "../../css/Base.module.css";
import loginstyle from "./Login.module.css";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";

const Login = ({ setUserState }) => {
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [user, setUserDetails] = useState({
    email: "",
    password: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "Please enter a valid email address";
    }
    if (!values.password) {
      error.password = "Password is required";
    }
    return error;
  };

  const loginHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));
    setIsSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://127.0.0.1:5000/login", user, {
        headers: {
          'Content-Type': 'application/json',
        },
      }).then((res) => {
        if (res.data.success) {
          setUserState(res.data.user);
          localStorage.setItem("token", res.data.token);
          navigate("/welcome", { replace: true });
        } else {
          alert(res.data.message || "Login failed");
        }
        setIsSubmit(false);
      }).catch((error) => {
        alert("Error: " + error.message);
        setIsSubmit(false);
      });
    }
  }, [formErrors, isSubmit, navigate, setUserState, user]);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token');
    if (token) {
      localStorage.setItem("token", token);
      axios.get("http://127.0.0.1:5000/user", {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      }).then((res) => {
        setUserState(res.data);
        navigate("/welcome", { replace: true });
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  }, [navigate, setUserState]);

  const handleGoogleSuccess = (response) => {
    console.log("Google login response:", response);
    if (response.credential) {
      axios.post(`http://127.0.0.1:5000/auth/google/callback`, { code: response.credential }, {
        headers: {
          'Content-Type': 'application/json',
        },
      }).then((res) => {
        setUserState(res.data.user);
        localStorage.setItem("token", res.data.token);
        navigate("/welcome", { replace: true });
      }).catch((error) => {
        console.error("Error during Google OAuth callback:", error.response.data);
        alert("Error: " + error.message);
      });
    } else {
      console.error("Google login failed: no credential returned");
      alert("Google login failed: no credential returned");
    }
  };


  const handleGoogleFailure = (error) => {
    alert("Google login failed");
  };

  return (
    <GoogleOAuthProvider clientId="46755883129-6s7civ58e0e28egeb9vecbt7c6b0mr7b.apps.googleusercontent.com">
      <div className={loginstyle.login}>
        <form onSubmit={loginHandler}>
          <button className={loginstyle["close-button"]}>×</button>
          <h1>Login</h1>
          <div className={loginstyle.inputContainer}>
            <label>Email</label>
            <input
              type="email"
              name="email"
              placeholder="Enter Email"
              onChange={changeHandler}
              value={user.email}
              className={loginstyle.inputField}
            />
            <p className={basestyle.error}>{formErrors.email}</p>
          </div>
          <div className={loginstyle.inputContainer}>
            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter Password"
              onChange={changeHandler}
              value={user.password}
              className={loginstyle.inputField}
            />
            <p className={basestyle.error}>{formErrors.password}</p>
          </div>
          <div className={loginstyle["remember-me"]}>
            <input type="checkbox" id="rememberMe" name="rememberMe" />
            <label htmlFor="rememberMe">Remember me</label>
          </div>
          <button className={`${basestyle.button_common} ${loginstyle.loginButton}`} type="submit">
            Login
          </button>
          <NavLink to="/forgot-password" className={loginstyle.forgotPassword}>
            Forgot password?
          </NavLink>

          <div className={loginstyle.newUser}>
            <span>New user?</span>
            <NavLink to="/signup" className={loginstyle.signupButton}>
              Signup Now
            </NavLink>
          </div>
          <div className={loginstyle.oauth}>
            <GoogleLogin
              onSuccess={handleGoogleSuccess}
              onError={handleGoogleFailure}
            />
          </div>
        </form>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
