import React, { useState } from "react";
import basestyle from "../../css/Base.module.css";
import forgotstyle from "./ForgotPassword.module.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ForgotPassword = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [message, setMessage] = useState("");

  const changeHandler = (e) => {
    setEmail(e.target.value);
  };

  const validateForm = (value) => {
    const error = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!value) {
      error.email = "Email is required";
    } else if (!regex.test(value)) {
      error.email = "Please enter a valid email address";
    }
    return error;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(email));

    if (Object.keys(formErrors).length === 0) {
      axios.post("http://127.0.0.1:5000/forgot-password", { email })
        .then((res) => {
          setMessage("Password reset link has been sent to your email.");
        })
        .catch((error) => {
          setMessage("Error sending email. Please try again.");
        });
    }
  };

  return (
    <div className={forgotstyle.forgotPassword}>
      <form onSubmit={submitHandler}>
        <h1>Forgot Password</h1>
        <div className={forgotstyle.inputContainer}>
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            onChange={changeHandler}
            value={email}
            className={forgotstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
        </div>
        <button className={`${basestyle.button_common} ${forgotstyle.submitButton}`} type="submit">
          Submit
        </button>
        <p className={forgotstyle.message}>{message}</p>
      </form>
    </div>
  );
};

export default ForgotPassword;
