import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import welcomestyle from "./Welcome.module.css";
import axios from 'axios';

const Welcome = ({ user, setUserState }) => {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');

  const logout = () => {
    setUserState({});
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  useEffect(() => {
    axios.get("http://ec2-3-107-93-162.ap-southeast-2.compute.amazonaws.com:5000/books", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  }, []);

  const searchHandler = (e) => {
    e.preventDefault();
    axios.get(`http://ec2-3-107-93-162.ap-southeast-2.compute.amazonaws.com:5000/books/search?q=${search}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleAddBook = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('file', file);

    axios.post("http://ec2-3-107-93-162.ap-southeast-2.compute.amazonaws.com:5000/add-book", formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        'Content-Type': 'multipart/form-data'
      }
    }).then(res => {
      alert(res.data.message);
      setTitle('');
      setAuthor('');
      setFile(null);
      setBooks([...books, { title, author, file_url: URL.createObjectURL(file) }]);
    }).catch(err => {
      console.error(err);
      alert('Error adding book');
    });
  };

  return (
    <div className={welcomestyle.welcome}>
      <h1>Welcome, {user.fname}</h1>
      <button onClick={logout}>Logout</button>

      <form onSubmit={searchHandler}>
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search books..."
        />
        <button type="submit">Search</button>
      </form>

      {user.is_admin && (
        <form onSubmit={handleAddBook}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Book Title"
          />
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            placeholder="Author"
          />
          <input
            type="file"
            onChange={handleFileChange}
          />
          <button type="submit">Add Book</button>
        </form>
      )}

      <ul>
        {books.map(book => (
          <li key={book.id}>
            <h2>{book.title}</h2>
            <p>{book.author}</p>
            <a href={book.file_url} download>Download</a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Welcome;
