import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import welcomestyle from "./Welcome.module.css";
import axios from 'axios';

const Welcome = ({ user, setUserState }) => {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    console.log("User data in Welcome component:", user);
    if (user) {
      axios.get("http://127.0.0.1:5000/books", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      }).then(res => {
        console.log("Books fetched:", res.data);
        setBooks(res.data);
      }).catch(err => {
        console.error("Error fetching books:", err);
      });
    }
  }, [user]);

  const logout = () => {
    setUserState(null);
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  const searchHandler = (e) => {
    e.preventDefault();
    axios.get(`http://127.0.0.1:5000/books/search?q=${search}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error("Error searching books:", err);
    });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleAddBook = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('author', author);

    axios.post("http://127.0.0.1:5000/books", formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    }).then((res) => {
      setMessage(res.data.message);
      setBooks([...books, res.data.book]);
    }).catch((err) => {
      setMessage(err.response.data.message);
    });
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  console.log("User is admin:", user.is_admin); // Debugging line to check if user is admin

  return (
    <div className={welcomestyle.welcome}>
      <h1>Welcome, {user.fname} {user.lname}</h1>
      <h2>User Privileges: {user.is_admin ? "Admin" : "Normal User"}</h2> {/* Debugging line to print user privileges */}
      <button onClick={logout}>Logout</button>

      {user.is_admin && (
        <div className={welcomestyle.adminSection}>
          <h2>Add Book</h2>
          <form onSubmit={handleAddBook}>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              type="text"
              placeholder="Author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
            <input
              type="file"
              onChange={handleFileChange}
            />
            <button type="submit">Add Book</button>
          </form>
          {message && <p>{message}</p>}
        </div>
      )}

      <div className={welcomestyle.searchSection}>
        <h2>Books</h2>
        <form onSubmit={searchHandler}>
          <input
            type="text"
            placeholder="Search books"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        <ul>
          {books.map(book => (
            <li key={book.id}>
              {book.title} by {book.author} - <a href={book.file_url} download>Download</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Welcome;
