import React, { useState, useEffect } from "react";
import basestyle from "../../css/Base.module.css";
import loginstyle from "./Login.module.css";
import { useNavigate, NavLink } from "react-router-dom";
import { GoogleLogin } from '@react-oauth/google';
import axios from "axios";

const Login = ({ setUserState }) => {
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [user, setUserDetails] = useState({
    email: "",
    password: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s+@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "Please enter a valid email address";
    }
    if (!values.password) {
      error.password = "Password is required";
    }
    return error;
  };

  const loginHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));
    setIsSubmit(true);
  };

  const handleGoogleLoginSuccess = (response) => {
    axios.post("http://127.0.0.1:5000/authorize/google", {
      token: response.credential
    }).then((res) => {
      if (res.data.token) {
        setUserState(res.data.user);
        localStorage.setItem('token', res.data.token);
        navigate("/", { replace: true });
      } else {
        alert("Google login failed");
      }
    }).catch((error) => {
      alert("Error: " + error.message);
    });
  };

  const handleGoogleLoginFailure = (response) => {
    alert("Google login failed: " + response.error);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://127.0.0.1:5000/login", user).then((res) => {
        if (res.data.token) {
          setUserState(res.data.user);
          localStorage.setItem('token', res.data.token);
          navigate("/", { replace: true });
        } else {
          alert(res.data.message || "Login failed");
        }
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  }, [formErrors, isSubmit, navigate, setUserState, user]);

  return (
    <div className={loginstyle.login}>
      <form>
        <h1>Login</h1>
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={changeHandler}
          value={user.email}
        />
        <p className={basestyle.error}>{formErrors.email}</p>
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={changeHandler}
          value={user.password}
        />
        <p className={basestyle.error}>{formErrors.password}</p>
        <button className={basestyle.button_common} onClick={loginHandler}>
          Login
        </button>
        <GoogleLogin
          onSuccess={handleGoogleLoginSuccess}
          onFailure={handleGoogleLoginFailure}
        />
      </form>
      <NavLink to="/signup">Not yet registered? Register Now</NavLink>
    </div>
  );
};

export default Login;
