import React, { useState } from "react";
import basestyle from "../../css/Base.module.css";
import forgotpasswordstyle from "./ForgotPassword.module.css";
import axios from "axios";
import { NavLink } from "react-router-dom";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [message, setMessage] = useState("");

  const changeHandler = (e) => {
    const { value } = e.target;
    setEmail(value);
  };

  const validateForm = (value) => {
    const error = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!value) {
      error.email = "Email is required";
    } else if (!regex.test(value)) {
      error.email = "This is not a valid email format!";
    }
    return error;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(email));
    setIsSubmit(true);
  };

  useState(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://127.0.0.1:5000/forgot-password", { email })
        .then((res) => {
          setMessage("Password reset email sent. Please check your inbox.");
        })
        .catch((error) => {
          setMessage("Error sending password reset email. Please try again.");
        });
    }
  }, [formErrors]);

  return (
    <div className={forgotpasswordstyle.forgotpassword}>
      <form onSubmit={submitHandler}>
        <h1>Forgot Password</h1>
        <div className={forgotpasswordstyle.inputContainer}>
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            onChange={changeHandler}
            value={email}
            className={forgotpasswordstyle.inputField}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
        </div>
        <button className={`${basestyle.button_common} ${forgotpasswordstyle.submitButton}`} type="submit">
          Submit
        </button>
        <p className={forgotpasswordstyle.message}>{message}</p>
        <NavLink to="/login" className={forgotpasswordstyle.backToLogin}>Back to Login</NavLink>
      </form>
    </div>
  );
};

export default ForgotPassword;
