import React, { useState, useEffect } from "react";
import basestyle from "../../css/Base.module.css";
import loginstyle from "./Login.module.css";
import axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";

const Login = ({ setUserState }) => {
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [user, setUserDetails] = useState({
    email: "",
    password: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "Please enter a valid email address";
    }
    if (!values.password) {
      error.password = "Password is required";
    }
    return error;
  };

  const loginHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));
    setIsSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      axios.post("http://3.27.222.116/login", user).then((res) => {
        if (res.data.success) {
          setUserState(res.data.user);
          navigate("/", { replace: true });
        } else {
          alert(res.data.message || "Login failed");
        }
      }).catch((error) => {
        alert("Error: " + error.message);
      });
    }
  }, [formErrors, isSubmit, navigate, setUserState, user]);

  return (
    <div className={loginstyle.login}>
      <form>
        <h1>Login</h1>
        <div className={loginstyle.inputContainer}>
          <label>Username</label>
          <input
            type="email"
            name="email"
            placeholder="Enter Username or Email"
            onChange={changeHandler}
            value={user.email}
          />
          <p className={basestyle.error}>{formErrors.email}</p>
        </div>
        <div className={loginstyle.inputContainer}>
          <label>Password</label>
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            onChange={changeHandler}
            value={user.password}
          />
          <p className={basestyle.error}>{formErrors.password}</p>
        </div>
        <button className={loginstyle.loginButton} onClick={loginHandler}>
          Login
        </button>
        <NavLink to="/forgot-password" className={loginstyle.forgotPassword}>
          Forgot password?
        </NavLink>
        <div className={loginstyle.newUser}>
          <span>New user?</span>
          <NavLink to="/signup" className={loginstyle.signupButton}>
            Signup Now
          </NavLink>
        </div>
        <div className={loginstyle.socialLogin}>
          <span>May also signup with</span>
          <div className={loginstyle.socialIcons}>
            <i className="fab fa-facebook"></i>
            <i className="fab fa-twitter"></i>
            <i className="fab fa-linkedin"></i>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
