import React, { useState, useEffect } from 'react';
import axios from 'axios';
import welcomeStyle from './Welcome.module.css';
import { useNavigate } from 'react-router-dom';

const Welcome = ({ user, setUserState }) => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/books', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  }, []);

  const searchHandler = (e) => {
    e.preventDefault();
    axios.get(`http://127.0.0.1:5000/books/search?q=${search}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    }).then(res => {
      setBooks(res.data);
    }).catch(err => {
      console.error(err);
    });
  };

  const uploadBook = async (bookFile, title, author) => {
    const token = localStorage.getItem('token'); // Retrieve token from local storage

    const formData = new FormData();
    formData.append('file', bookFile);
    formData.append('title', title);
    formData.append('author', author);

    try {
      const response = await axios.post('http://127.0.0.1:5000/add-book', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}` // Include the token in the request header
        }
      });
      console.log(response.data);
    } catch (error) {
      console.error('Error uploading book:', error);
    }
  };

  const logout = () => {
    setUserState({});
    localStorage.removeItem('token');
    navigate('/login', { replace: true });
  };

  return (
    <div className={welcomeStyle.welcome}>
      <h1>Welcome, {user.fname} {user.lname}</h1>
      <button onClick={logout}>Logout</button>

      {user.is_admin && (
        <div className={welcomeStyle.adminSection}>
          <h2>Add Book</h2>
          <form onSubmit={addBookHandler}>
            <div>
              <label>Title</label>
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>
            <div>
              <label>Author</label>
              <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
            </div>
            <div>
              <label>File</label>
              <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
            </div>
            <button type="submit">Add Book</button>
          </form>
        </div>
      )}

      <div className={welcomeStyle.searchSection}>
        <h2>Search Books</h2>
        <form onSubmit={searchHandler}>
          <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} />
          <button type="submit">Search</button>
        </form>
        <div className={welcomeStyle.booksList}>
          {books.map(book => (
            <div key={book.id}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <a href={book.file_url} download>Download</a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Welcome;
